let topicSplit = msg.topic.split("/");

let nodeId = String(topicSplit[1]);
let unitId = String(topicSplit[2]);
let data = msg.payload

if (topicSplit[3] == "distance"){
    msg.payload = [
        { // Fields
            distance_cm: data
        },
        { // Tags
            node_id: nodeId,
            unit_id: unitId,

        }
    ];
} else if (topicSplit[3] == "state"){
    msg.payload = [
        { // Fields
            state: data
        },
        { // Tags
            node_id: nodeId,
            unit_id: unitId,

        }
    ];

    let allUnitStates = global.get('allUnitStates') || {}; // Obtiene el objeto, o crea uno si no existe
    if (!allUnitStates[nodeId]) {
        allUnitStates[nodeId] = {}; // Crea un objeto para este nodo si no existe
    }
    allUnitStates[nodeId][unitId] = data; // Guarda el estado de esta unidad
    global.set('allUnitStates', allUnitStates); // Actualiza la variable de contexto
}

return msg;

let allUnitStates = global.get('allUnitStates') || {}; // Obtiene el objeto con los últimos estados

let libreCount = 0;
let ocupadoCount = 0;
let potencialmenteLibreCount = 0;
let totalUnits = 0;

// Itera sobre todos los nodos y sus unidades
for (let nodeId in allUnitStates) {
    if (allUnitStates.hasOwnProperty(nodeId)) {
        let unitsInNode = allUnitStates[nodeId];
        for (let unitId in unitsInNode) {
            if (unitsInNode.hasOwnProperty(unitId)) {
                totalUnits++;
                let state = unitsInNode[unitId];
                switch (state) {
                    case "Libre":
                        libreCount++;
                        break;
                    case "Ocupado":
                        ocupadoCount++;
                        break;
                    case "Potencialmente libre":
                        potencialmenteLibreCount++;
                        break;
                    // No necesitas un default si ya sabes que los estados son esos 3
                }
            }
        }
    }
}

// Crea el payload para el resumen (puede ser para MQTT o directamente para InfluxDB)
if (totalUnits === 0) {
    return null; // No envía mensaje
}

// Prepara el payload para InfluxDB (medición 'system_summary')
// Usando el formato de objeto único si el Measurement se define en el payload
msg.payload = [
    {
        total_units: totalUnits,
        libre_count: libreCount,
        ocupado_count: ocupadoCount,
        potencialmente_libre_count: potencialmenteLibreCount
    },
    {
        summary_type: "current_state_distribution", // Tag para identificar el tipo de resumen
    }
]

return msg;

const SIMULATED_UNITS = ["A", "B", "C", "D"]; // IDs de las unidades por cada nodo
const UMBRAL_OCUPADO_CM = 85; // Simula un umbral para 'ocupado'
const UMBRAL_POTENCIALMENTE_LIBRE_CM = 95; // Simula un umbral para 'potencialmente libre'

let messages = []; // Array para almacenar los mensajes a enviar
for (let nodeId = 10; nodeId < 37; nodeId++) {
    SIMULATED_UNITS.forEach(unitId => {
        let simulatedState;

        const randomChance = Math.random();

        if (randomChance < 0.4) { // 40% de probabilidad de ser ocupado;
            simulatedState = "Ocupado";
        } else if (randomChance < 0.6) { // 20% de probabilidad de ser potencialmente libre
            simulatedState = "Potencialmente libre";
        } else { // 40% de probabilidad de ser libre
            simulatedState = "Libre";
        }

        // Construir el mensaje para InfluxDB (formato de objeto único)
        let influxMsg = {
            payload: [
                {
                    state: simulatedState
                },
                {
                    node_id: String(nodeId),
                    unit_id: unitId
                }
            ]
        };
        messages.push(influxMsg);

        let allUnitStates = global.get('allUnitStates') || {}; // Obtiene el objeto, o crea uno si no existe
        if (!allUnitStates[nodeId]) {
            allUnitStates[nodeId] = {}; // Crea un objeto para este nodo si no existe
        }
        allUnitStates[nodeId][unitId] = simulatedState; // Guarda el estado de esta unidad
        global.set('allUnitStates', allUnitStates); // Actualiza la variable de contexto
    });
};

messages.forEach(msg => {
    node.send(msg);
});
