const allUnitStates = global.get('allUnitStates') || {};

function calculateSeatCoordinates(nodeId, unitId) {
    const TABLE = Math.floor((parseInt(nodeId) - 10) / 5);
    const PLACE = (parseInt(nodeId) - 10) % 5;
    const COL_INCREMENT_X = 92;  // Salto para pasar al extremo derecho
    const ROW_INCREMENT_Y = 32;  // Salto para pasar al extremo inferior
    const BASE_X = 225 + (155 * TABLE); // Extremo izquierdo de la mesa
    const BASE_Y = 845 + (32 * 2 * PLACE); // Extremo superior del nodo

    if (unitId === "A") {
        return { x: BASE_X, y: BASE_Y };
    }

    if (unitId === "B") {
        return { x: BASE_X + COL_INCREMENT_X, y: BASE_Y };
    }

    if (unitId === "C") {
        return { x: BASE_X, y: BASE_Y + ROW_INCREMENT_Y};
    }

    if (unitId === "D") {
        return { x: BASE_X + COL_INCREMENT_X, y: BASE_Y + ROW_INCREMENT_Y };
    }
}

const STATE_COLORS = {
    "Libre": "#00CC00", // Verde
    "Ocupado": "#FF0000", // Rojo
    "Potencialmente libre": "#FFA500" // Naranja
};

const pointRadius = 6; // Radio de los círculos (el tamaño de los puntos)
const lineWidth = 15; // Ancho del borde
const borderColor = "#FFFFFF"; // Color del borde (blanco)

const annotations = [];

for (const nodeId in allUnitStates) {
    if (parseInt(nodeId) < 10) continue;
    if (allUnitStates.hasOwnProperty(nodeId)) {
        const unitsInNode = allUnitStates[nodeId]; // Unidades dentro de este nodo
        for (const unitId in unitsInNode) {
            if (unitsInNode.hasOwnProperty(unitId)) {
                const currentState = unitsInNode[unitId];
                const coords = calculateSeatCoordinates(nodeId, unitId);
                const color = STATE_COLORS[currentState]
                if (coords) {
                    annotations.push({
                        type: "circle",
                        x: coords.x,
                        y: coords.y,
                        r: pointRadius,
                        stroke: color,
                        lineWidth: lineWidth,
                    });
                } else {
                    node.warn(`Coordenadas no encontradas para la unidad: `+ nodeId + unitId);
                }
            }
        }
    }
}

msg.annotations = annotations;

return msg;

